mapper.insert:
INSERT INTO SUPPLY (
SUPPLY_NAME
, SUPPLY_PRICE
, SUPPLY_STANDARD
, SUPPLIER
, SUPPLY_CAUTION
, SUPPLY_MIN_AMOUNT
, SUPPLY_IMAGE
) VALUES (
#{supplyName}
, #{supplyPrice}
, #{supplyStandard}
, #{supplier}
, #{supplyCaution}
, #{supplyMinAmount}
, #{supplyImage}
)

INSERT INTO order_info(
ORDER_MANAGER
, ORDER_DATE
, DELIVERY_DATE
, ORDER_NOTE
) VALUES (
#{orderManager}
, NOW()
, #{deliveryDate}
, #{orderNote}
);

mapper.update:
UPDATE ORDER_INFO
SET ORDER_STATUS = '입고 완료'
WHERE ORDER_NUM = #{orderNum};

UPDATE ORDER_INFO
SET ORDER_STATUS = '취소 됨'
WHERE ORDER_NUM = #{orderNum};

UPDATE SUPPLY
SET SUPPLY_AMOUNT = SUPPLY_AMOUNT + (
SELECT ORDER_AMOUNT
FROM order_SUPPLY_info
WHERE ORDER_NUM = #{orderNum} AND SUPPLY_NUM = SUPPLY.SUPPLY_NUM
)
WHERE SUPPLY_NUM IN (
SELECT SUPPLY_NUM
FROM order_SUPPLY_info
WHERE ORDER_NUM = #{orderNum}
);
